mapper.select:
select i.*, m.merchant_name from inventory i LEFT JOIN merchant m on i.merchant_id = m.merchant_id where 1=1
and inventory_name like concat('%',#{inventory.inventoryName},'%')
${queryWrapper.customSqlSegment}

select distinct i.*, i.id as 'key', i.inventory_name as 'title', i.inventory_information as 'content', group_concat(ica.inventory_category_id) as 'inventoryCategory' from inventory i LEFT JOIN inventory_category_associate ica on ica.inventory_id = i.id where 1=1
and i.merchant_id = #{inventory.merchantId}
group by i.id
