mapper.select:
select i.*, m.merchant_name from inventory i LEFT JOIN merchant m on i.merchant_id = m.merchant_id where 1=1
and inventory_name like concat('%',#{inventory.inventoryName},'%')
${queryWrapper.customSqlSegment}

select i.inventory_name as 'responseEntity' from inventory i where 1=1 and i.sts != '-1'
and i.inventory_name like concat('%',#{str},'%') limit 0,#{num}
order by rand() limit #{num}
