mapper.select:
select oi.*
from order_information oi
where 1=1

    and oi.create_by = #{orderInformation.createBy}


    and oi.merchant_id = #{orderInformation.merchantId}


    and oi.sts = #{orderInformation.sts}


    and oi.order_code = #{orderInformation.orderCode}

